-- CREATE TABLE --
CREATE TABLE people (                                   -- Crea una tabla con el nombre people datos                --
                                                        -- con las columnas                                         --
id Int NOT NULL AUTO_INCREMENT,                         -- 'id'; (Numeros Enteros),                                 --
surname VarChar(50) NOT NULL,                           -- 'surname'; (Un maximo de 50 caracteres)                  --
lastname VarChar(50),                                   -- 'lastname'; (Un maximo de 50 caracteres)                 --
CreationDate Date DEFAULT(CURRENT_DATE),               -- 'CreationDate'; (una fecha en formato YYYY-MM-DD)        --
age Int                                                 -- 'age'; (Numeros Enteros)                                 --
PRIMARY KEY(id)                                         --                                                          --
UNIQUE(id)                                              --                                                          --
CHECK(age >= 18)                                        --                                                          --
);                                                      --                                                          --
--------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------

-- MODIFICADORES --
NOT NULL -- El dato no podra tener el valor NULL (No podra estar vacio) --
--------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
UNIQUE('<Columnas>') -- El dato no podra repetirse en ningun rincon de la tabla --
--------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
PRIMARY KEY('<Columnas>') -- los datos de la columna seran Claves Primarias --
--------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
CHECK('<CondiciÃ³n>') -- Se comprueba una condicion para dar permisos a insertar los datos --
--------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
DEFAULT('<Dato por Defecto') -- Crea un dato por defecto en una columna --
--------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
AUTO_INCREMENT -- Incrementa el valor del dato en base al dato anterior --
--------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------

-- DROP TABLE --
DROP TABLE people; -- Eliminala  tabla con el nombre people --
--------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------

