--------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------

-- ADD --
ALTER TABLE people
ADD username varchar(150) UNIQUE; 
--------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
-- RENAME --
ALTER TABLE people
RENAME COLUMN lastname TO forename; 
--------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
-- MODIFY --
ALTER TABLE people
MODIFY COLUMN CreationDate Datetime DEFAULT(CURRENT_TIMESTAMP);
--------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------------
